mapper.select:
SELECT (row_number() over()) cnt, a.kwd_a, a.kwd_b, a.doc_date, a.doc_cnt_both, a.category_code, CASE LEFT(a.category_code, 5) WHEN '08201' THEN '1' WHEN '08202' THEN '2' END cId FROM (SELECT ka.kwd_a, ka.kwd_b, ka.doc_date, ka.doc_cnt_both, ka.category_code FROM dm.daily_kwd_trend_cnt_v2 ka
AND ka.fid = #{param.fid}
AND ka.doc_date BETWEEN '${param.startDate}' AND '${param.endDate}'
AND ka.kwd_a = #{param.kwd} AND ka.business_code = #{param.businessCode} AND ka.category_code LIKE #{param.categoryCode} || '%'
ORDER BY ka.doc_date, ka.doc_cnt_both DESC ) a ;

SELECT ka_rn.cnt, ka_rn.kwd_a, ka_rn.kwd_b, ka_rn.doc_date, ka_rn.doc_cnt_both FROM (SELECT (row_number() over(PARTITION BY a.doc_date)) cnt, a.kwd_a, a.kwd_b, a.doc_date, a.doc_cnt_both FROM (SELECT ka.kwd_a, ka.kwd_b, ka.doc_date, ka.doc_cnt_both FROM dm.monthly_kwd_trend_cnt_v2 ka
AND ka.fid = #{param.fid}
AND ka.doc_date BETWEEN #{param.startDate}::DATE AND #{param.endDate}::DATE
AND ka.kwd_a = #{param.kwd} AND ka.category_code LIKE #{param.categoryCode} || '%' AND ka.business_code = #{param.businessCode}
ORDER BY ka.doc_date, ka.doc_cnt_both DESC ) a ) ka_rn WHERE cnt <= 20

    SELECT *
	FROM(
			SELECT (row_number() over(PARTITION BY a.doc_date ORDER BY a.docCntBoth DESC)) rn, a.*
			FROM (
					SELECT doc_cnt_both docCntBoth, kwd_a, kwd_b, doc_date
					FROM dm.weekly_kwd_trend_cnt_v2 ktc
					
						
							 AND ktc.fid = #{param.fid} 
							
								
							 		AND ktc.doc_date BETWEEN #{param.startDate}::DATE AND #{param.endDate}::DATE
							 	 
							 
							 AND ktc.kwd_a = #{param.kwd} 
							 AND ktc.business_code  = #{param.businessCode}  
						 
						AND ktc.doc_cnt_both != 0
					
				) a
			) RESULT	
	
		
			
				rn <= #{param.cnt}::INTEGER
