mapper.select:
SELECT RESULT.* FROM( SELECT row_number() over() rn , rel_doc_uid FROM dm.stock_market_top_rel_docs trd
AND trd.isu_dcs_date = #{param.date}::DATE AND trd.isu_cd = #{param.scriptUid} AND trd.fid = #{param.fid}
) RESULT
AND RESULT.rn <= #{param.cnt}::integer

SELECT stdInfo.script_uid, stdInfo.category_code, CASE stdInfo.fid -- 은행 WHEN 'BDPC04010101' THEN '1' WHEN 'BDPC04010201' THEN '2' -- 카드 WHEN 'BDPC04020101' THEN '1' WHEN 'BDPC04020201' THEN '2' -- 금투 WHEN 'BDPC04030101' THEN '1' WHEN 'BDPC04030201' THEN '4' WHEN 'BDPC04030301' THEN '5' -- 생명 WHEN 'BDPC04040101' THEN '1' WHEN 'BDPC04040201' THEN '6' WHEN 'BDPC04040401' THEN '7' END cId, docs.rel_doc_title script_title, stdInfo.script_date, stdInfo.script_weight, stdInfo.script_rank, summaryEx.rel_doc_uid, summaryEx.sum_rate, summaryEx.rel_doc_summary, docs.rel_doc_rank FROM dm.script_rel_docs_std_info stdInfo INNER JOIN dm.script_rel_docs docs ON docs.job_id = stdinfo.job_id and docs.script_uid = stdInfo.script_uid and docs.business_code = stdInfo.business_code and docs.category_code = stdInfo.category_code and docs.script_date = stdInfo.script_date INNER JOIN dm.script_rel_docs_summary_extractive summaryEx ON summaryEx.job_id = docs.job_id and summaryEx.rel_doc_uid = docs.rel_doc_uid and summaryEx.business_code = docs.business_code and summaryEx.category_code = docs.category_code and summaryEx.script_date = docs.script_date
AND stdInfo.script_date = #{param.date}::DATE AND stdInfo.script_uid = #{param.scriptUid} AND docs.rel_doc_rank <= #{param.cnt}::integer AND stdInfo.category_code like #{param.categoryCode} || '%' AND stdInfo.business_code = #{param.businessCode} AND stdInfo.job_id = #{param.jobId} AND summaryEx.fid = #{param.fid}
ORDER BY stdInfo.category_code, docs.script_date DESC, docs.rel_doc_rank asc;
